import * as S from './styles';
import { Link } from 'react-router-dom';
import Avatar from 'react-avatar';
import Footer from './Footer';
import SubMenu from './SubMenu';
import tw from 'twin.macro';
import { ArchiveIcon, XIcon } from '@heroicons/react/outline';

const menus = [
  {
    title: 'Site Content',
    icon: <ArchiveIcon tw="h-6 w-6" />,
    items: [
      {
        title: 'Pages',
        target: '/pages',
      },
      {
        title: 'News',
        target: '/news',
      },
      {
        title: 'Flashes',
        target: '/flashes',
      },
      {
        title: 'Magazines',
        target: '/magazines',
      },
      {
        title: 'Fields',
        target: '/fields',
      },
    ],
  },
  {
    title: 'Media Library',
    icon: <ArchiveIcon tw="h-6 w-6" />,
    target: '/medialibrary',
    items: [],
  },
];

export const LogoWrapper = tw.div`
  flex justify-center items-center
  py-4
  px-4
`;

export const MainMenu = tw.div`
  px-4 py-6
`;

const Sidebar = ({ open, onClose = () => {} }) => {
  const listMenu = menus.map((menu) => {
    return <SubMenu menu={menu} />;
  });
  return (
    <S.Wrapper open={open}>
      <LogoWrapper>
        <Link to="/" tw="inline-flex flex-row items-center">
          <Avatar
            size="100"
            round
            name="Juan"
            src="https://secure.gravatar.com/avatar/8c5d4c4b9ef6c68c4ff91c319d4c56be?d=404&s=200"
          />
        </Link>
        <XIcon tw="h-6 w-6 sm:hidden" onClick={onClose} />
      </LogoWrapper>
      <div tw="flex flex-col">
        <p style={{ marginBottom: '5px' }}>Lorem ipsum</p>
        <p style={{ fontStyle: 'italic', fontSize: '15px' }}>Lorem@Ipsum.com</p>
      </div>
      <MainMenu>{listMenu}</MainMenu>
      <Footer />
    </S.Wrapper>
  );
};

export default Sidebar;
