import * as S from './styles';
import { Link } from 'react-router-dom';
import logo from '../../img/logo-helx.svg';
import Footer from './Footer';
import SubMenu from './SubMenu';
import tw from 'twin.macro';
import { ArchiveIcon, XIcon } from '@heroicons/react/outline';

const menus = [
  {
    title: 'Site Content',
    icon: <ArchiveIcon tw="h-6 w-6" />,
    items: [
      {
        title: 'Pages',
        target: '/pages',
      },
      {
        title: 'News',
        target: '/news',
      },
      {
        title: 'Flashes',
        target: '/flashes',
      },
      {
        title: 'Magazines',
        target: '/magazines',
      },
      {
        title: 'Fields',
        target: '/fields',
      },
    ],
  },
  {
    title: 'Media Library',
    icon: <ArchiveIcon tw="h-6 w-6" />,
    target: '/medialibrary',
    items: [],
  },
];

export const LogoWrapper = tw.div`
  flex justify-between items-center
  py-4
  px-4
`;

export const MainMenu = tw.div`
  px-4 py-6
`;

const Sidebar = ({ open, onClose = () => {} }) => {
  const listMenu = menus.map((menu) => {
    return <SubMenu menu={menu} />;
  });
  return (
    <S.Wrapper open={open}>
      <LogoWrapper>
        <Link to="/" tw="inline-flex flex-row items-center">
          <img src={logo} alt="logo" tw="w-16" />
          <div>
            <BrandName>HELX</BrandName>
            <Strapline>Website Manager</Strapline>
          </div>
        </Link>
        <XIcon tw="h-6 w-6 sm:hidden" onClick={onClose} />
      </LogoWrapper>
      <MainMenu>{listMenu}</MainMenu>
      <Footer />
    </S.Wrapper>
  );
};

export default Sidebar;
